import React from 'react'
import './Lightning.css'

export default class Lightning extends React.Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    var rand = Math.round(Math.random() * (30000 - 500)) + 500; // generate new time (between 3sec and 500"s)

      const lightningAnim = setInterval(() => {
        console.log('TRIGGERED')
        document.querySelector('.lightning').style.animation = 'lightningStrike 5s';
        setTimeout(() => {
          document.querySelector('.lightning').style.animation = null;
        }, 5000)
      }, rand)
  }
  render() {
    return(
      <div className="lightning" />
    )
  }
}
