import React from 'react'
import picOne from './1.jpg'
import picTwo from './2.jpg'
import picThree from './3.jpg'
import picFour from './4.jpg'
import picFive from './5.jpg'
import picSix from './6.jpg'
import picSeven from './7.jpg'
import picEight from './8.jpg'
import picNine from './9.jpg'
import picTen from './10.jpg'
import picEleven from './11.jpg'
import picTwelve from './12.jpg'
import picThirteen from './13.jpg'
import picFourteen from './14.jpg'



import ('./Pics.css')

export default class Pics extends React.Component {
  constructor(props) {
    super(props)
    this.PicChooser = this.PicChooser.bind(this)
    this.state = {
      pics: null
    }
  }

  componentDidUpdate() {
    /*setTimeout(() => {
    setTimeout(this.PicChooser, 6000)
  })*/
}

  componentDidMount() {
    setInterval(this.PicChooser, 5000)
  }

  PicChooser() {
    const pics = [
      picOne,
      picTwo,
      picThree,
      picFour
      /*vidOne,
      vidTwo,
      vidThree*/
    ]
    const chosenOne = pics[Math.floor(Math.random() * pics.length)]
    document.querySelector('.Pics').classList.add('opacity0')
    document.querySelector('.Pics').ontransitionend = () => {
      this.setState({
        pic: chosenOne
      })
    }
    setTimeout(() => {
      document.querySelector('.Pics').classList.remove('opacity0')
    }, 1500)
}

  render() {
    return(
      <div className="Pics" >
      <div className="picContainer" style={{backgroundImage: `url('${this.state.pic}')`}}></div>
      </div>
    )
  }
}
