import React from 'react'
import WeatherTile from './WeatherTile'
import DayNightCycle from './DayNightCycle'
//Cloud modes
import CloudsCloudy from './WeatherComponents/Clouds/CloudsCloudy'
import PartlyCloudy from './WeatherComponents/Clouds/PartlyCloudy'
import Overcast from './WeatherComponents/Clouds/Overcast'
import WindyClouds from './WeatherComponents/Clouds/WindyClouds'
//Snow modes
import LightSnow from './WeatherComponents/Snow/LightSnow'
import MedSnow from './WeatherComponents/Snow/MedSnow'
import HeavySnow from './WeatherComponents/Snow/HeavySnow'
import Blizzard from './WeatherComponents/Snow/Blizzard'

//Rain modes
import LightRain from './WeatherComponents/Rain/LightRain'
import MedRain from './WeatherComponents/Rain/MedRain'
import HeavyRain from './WeatherComponents/Rain/HeavyRain'
import TorrRain from './WeatherComponents/Rain/TorrRain'

//Storm modes
import Lightning from './WeatherComponents/Thunderstorm/Lightning'

import Fog from './WeatherComponents/Mist&Fog/Fog'
import Mist from './WeatherComponents/Mist&Fog/Mist'

import './WeatherSection.css'
import moment from 'moment'

class WeatherSection extends React.Component {
  constructor(props) {
    super(props)
    this.RequestWeather = this.RequestWeather.bind(this)
    this.Sunset = this.Sunset.bind(this)
    //this.Sunrise = this.Sunrise.bind(this)
    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
    }
    console.log('DGFGFGFGFG: ' + parseInt(moment().format('HH')))
  }

  RequestWeather() {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=2`)
    //fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=${this.state.location}&days=5`)
    .then(response => response.json())
    .then(json => {
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
        })
        this.checkSunState()

      }
    ).catch(() => {
      console.log('error')
    })
  }

  componentDidMount() {
    this.RequestWeather()
    setInterval(this.checkSunState, (60000 * 5))
    setInterval(this.RequestWeather, 120000)
  }

  Sunset() {
    const now = moment().format.valueOf();
    const sunsetStartTime = this.state.weatherData[0].astro.sunset.split(':')[0]
    const sunriseStartTime = this.state.weatherData[0].astro.sunrise.split(':')[0]
    let sunrise;
    let sunriseTimer;
    let sunset;
    let sunsetTimer;

    if (this.state.currentData.is_day == 0) {
      if (now < moment(`${this.state.weatherData[0].date} ${this.state.weatherData[1].astro.sunrise}`, 'YYYY-MM-DD hh:mm A').subtract(15, 'minutes').valueOf()) {
        sunrise = moment(`${this.state.weatherData[0].date} ${this.state.weatherData[1].astro.sunrise}`, 'YYYY-MM-DD hh:mm A').subtract(15, 'minutes').valueOf();
      } else {
        sunrise = moment(`${this.state.weatherData[1].date} ${this.state.weatherData[1].astro.sunrise}`, 'YYYY-MM-DD hh:mm A').subtract(15, 'minutes').valueOf();
      }
      sunriseTimer = setTimeout(Sunrise, parseInt(sunrise - now));

    } else if (this.state.currentData.is_day == 1) {
        if (now < moment(`${this.state.weatherData[0].date} ${this.state.weatherData[1].astro.sunrise}`, 'YYYY-MM-DD hh:mm A').subtract(15, 'minutes').valueOf()) {
          sunset = moment(`${this.state.weatherData[0].date} ${this.state.weatherData[1].astro.sunrise}`, 'YYYY-MM-DD hh:mm A').subtract(15, 'minutes').valueOf();
        } else {
          sunset = moment(`${this.state.weatherData[1].date} ${this.state.weatherData[1].astro.sunrise}`, 'YYYY-MM-DD hh:mm A').subtract(15, 'minutes').valueOf();
        }
          sunsetTimer = setTimeout(() => {
            document.querySelector('.dayNightCircle')
            .style.animation = 'dayNightCycle 1800s 1s forward';
            document.querySelector('.dayNightCircle')
            .classList.add('nightTime');
          }, parseInt(sunset - now));
        }
    /*console.log(moment(`${this.state.weatherData[0].date} ${this.state.weatherData[0].astro.sunset}`, 'YYYY-MM-DD hh:mm A').valueOf())
    console.log(moment().valueOf())
    const now = moment();
    const sunsetStartTime = this.state.weatherData[0].astro.sunset.split(':')
    const sunsetRef = new Date();
    if ((sunsetRef.getHours() - 12) === parseInt(sunsetStartTime[0]) &&
      sunsetRef.getMinutes() === (parseInt(sunsetStartTime[1].split(' ')[0]) - 15)) {
          document.querySelector('.dayNightCircle')
          .style.animation = 'dayNightCycle 12s infinite 1s forward';
      }*/
    }

  Sunrise() {
    const sunriseStartTime = this.state.weatherData[0].astro.sunset.split(':')
    const sunriseRef = new Date();
    if (sunriseRef.getHours() === parseInt(sunriseStartTime[0]) &&
      sunriseRef.getMinutes() === (parseInt(sunriseStartTime[1].split(' ')[0]) - 15)) {
          document.querySelector('.dayNightCircle')
          .style.animation = 'dayNightCycle 30s infinite 1s forward';
      }
  }

  render() {
    return(
    <div className="weatherBackground">
    {this.state.isLoading ?
      <DayNightCycle is_day={this.state.currentData.is_day}/> : null}
      <PartlyCloudy />
      <Fog />
    <div className="Tiles">
    <div className="nowWeather">
    {this.state.isLoading ?
    <WeatherTile icon={this.state.currentData.condition.icon} date="Now" today={this.state.currentData.temp_c} /> : null}
    </div>
    <div className="forecast">
  {this.state.isLoading ?
    this.state.weatherData.map(data => (<WeatherTile icon={data.day.condition.icon} date={moment(data.date, 'YYYY-MM-DD').format('MMM D')} high={data.day.maxtemp_c} low={data.day.mintemp_c}/>)) : null }
    </div>
    </div>
    </div>
    )
  }
}
export default WeatherSection

