import React from 'react'
import './DayNightCycle.css'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';
const moment = extendMoment(Moment);

export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      sunPosition: null,
      sunTimes: null
    }
    //this.checkSunState = this.checkSunState.bind(this)
    this.SunPos = this.SunPos.bind(this)
    this.InitSun = this.InitSun.bind(this)
  }

  SunPos() {
    const sun = document.querySelector('.Sun')
    const sky = document.querySelector('day')
    const sunYellow = document.querySelector('.sunYellow')
    const sunOrange = document.querySelector('.sunOrange')
    const sunRed = document.querySelector('.sunRed')

    const positions = ['noon', 'goldenHour', 'sunsetStart', 'sunset', 'dusk', 'nauticalDusk', '' ]
    const now = new Date()
    this.setState({
      sunTimes:suncalc.getTimes(now, 49.895077, -97.138451)
    })
    const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const sunTimes = Object.values(sunPos)
    for (let i = 0; i < sunTimes.length; i++) {
      if (now.getHours() == sunTimes[i].getHours()) {
        if (now.getMinutes() == sunTimes[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] == sunTimes[i])  {
                this.setState({
                  sunPosition: ii
                })
              }
            }
          }
        }
      }
    }
    if (this.state.sunPosition !== null) {
      if (this.state.sunPosition == 'noon') {

      } else if (this.state.sunPosition == 'goldenHour' || this.state.sunPosition == 'goldenHourEnd') {
          sky.classList.add('opacity3')
          sun.classList.add('goldenHour')
          sunYellow.classList.add('opacity2')
          sunOrange.classList.add('opacity2')

      } else if (this.state.sunPosition == 'sunsetStart' || this.state.sunPosition == 'sunriseEnd') {
        sky.classList.remove('opacity3')
        sky.classList.add('opacity2')
        sun.classList.remove('goldenHour')
        sun.add('sunsetStart')
        sunRed.classList.add('opacity2')

      } else if (this.state.sunPosition == 'sunset' || this.state.sunPosition == 'sunrise') {
          sky.classList.remove('opacity2')
          sky.classList.add('opacity1')
          sun.classList.add('sunset')
          sunYellow.classList.remove('opacity2')
          sunYellow.classList.add('opacity1')
          sunOrange.classList.remove('opacity2')
          sunOrange.classList.add('opacity3')
          sunRed.classList.remove('opacity2')
          sunRed.classList.add('opacity3')
      } else if ((this.state.sunPosition == 'dusk' || this.state.sunPosition == 'nauticalDusk') || (this.state.sunPosition == 'dawn' || this.state.sunPosition == 'nauticalDawn'))
          sky.classList.remove('opacity1')
          sky.classList.add('opacity0')
          sun.classList.remove('sunset')
          sun.classList.add('dusk')
          sunYellow.classList.remove('opacity1')
          sunYellow.classList.add('opacity0')
          sunOrange.classList.remove('opacity3')
          sunOrange.classList.add('opacity1')
      } else if (this.state.sunPosition == 'night' || this.state.sunPosition == 'nightEnd') {
          sun.classList.remove('dusk')
          sun.classList.add('night')
          sunOrange.classList.remove('opacity1')
          sunOrange.classList.add('opacity0')
          sunRed.classList.remove('opacity3')
          sunRed.classList.add('opacity1')
      } else if (this.state.sunPosition == 'natis') {
          sky.classList.add('skyNatis')
      }
    }


  componentDidMount() {
    this.InitSun()
    console.log(suncalc.getTimes(new Date(), 49.895077, -97.138451))
    console.log(new Date())
    //this.checkSunState();
    const sunPosTimer = setInterval(this.SunPos, 30000)
  }

  InitSun() {
    const now = moment(new Date(), 'ddd MMM DD YYYY HH:mm')
    const sunTimes = suncalc.getTimes(now, 49.895077, -97.138451)
    const times = [
      moment(sunTimes.sunrise, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunriseEnd, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.goldenHourEnd, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.solarNoon, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.goldenHour, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunsetStart, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunset, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.dusk, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nauticalDusk, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.night, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nadir, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nightEnd, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nauticalDawn, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.dawn, 'ddd MMM DD YYYY HH:mm')
    ]
    const ranges = [
      moment.range(times[0], times[1]),
      moment.range(times[1], times[2]),
      moment.range(times[2], times[3]),
      moment.range(times[3], times[4]),
      moment.range(times[4], times[5]),
      moment.range(times[5], times[6]),
      moment.range(times[6], times[7]),
      moment.range(times[7], times[8]),
      moment.range(times[8], times[9]),
      moment.range(times[9], times[10]),
      moment.range(times[10], times[11]),
      moment.range(times[11], times[12]),
      moment.range(times[12], times[13]),
      moment.range(times[13], times[13].add(1, 'minutes'))
    ];

    for (let i = 0; i < ranges.length; i++) {
      if (now.within(ranges[i])) {
        if (i == 0) {
          this.setState({
            sunPosition: 'sunrise'
          })
        }
          else if (i == 1) {
            this.setState({
              sunPosition: 'sunriseEnd'
            })
          }
        else if (i == 2) {
          this.setState({
            sunPosition: 'goldenHourEnd'
          })
        }
      else if (i == 3) {
        this.setState({
          sunPosition: 'solarNoon'
        })
      }
        else if (i == 4) {
          this.setState({
            sunPosition: 'goldenHour'
          })
        }
        else if (i == 5) {
          this.setState({
            sunPosition: 'sunsetStart'
          })
        }
        else if (i == 6) {
          this.setState({
            sunPosition: 'sunset'
          })
        }
          if (i == 7) {
            this.setState({
              sunPosition: 'dusk'
            })
          }
        else if (i == 8) {
          this.setState({
            sunPosition: 'nauticalDusk'
          })
        }
      else if (i == 9) {
        this.setState({
          sunPosition: 'night'
        })
      }
        else if (i == 10) {
          this.setState({
            sunPosition: 'nadir'
          })
        }
        else if (i == 11) {
          this.setState({
            sunPosition: 'nightEnd'
          })
        }
        else if (i == 12) {
          this.setState({
            sunPosition: 'nauticalDawn'
          })
        }
        else if (i == 13) {
          this.setState({
            sunPosition: 'dawn'
          })
        }
      }
    }
    console.log('TIME: ' + this.state.sunPosition)

  }
  render() {
    return(
      <div className="night">
      <div className="day">
      </div>
        <div className="Sun">
          <div className="sunYellow"></div>
          <div className="sunOrange"></div>
          <div className="sunRed"></div>
        </div>
        <div className="Moon"></div>
      </div>
    )
  }
}
