import React from 'react'
import './DayNightCycle.css'
import moment from 'moment'


export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.checkSunState = this.checkSunState.bind(this)
  }

  checkSunState() {
    if (this.props.is_day == 0) {
      if (!document.querySelector('.dayNightCircle')
        .classList.contains('nightTime')) {
          document.querySelector('.dayNightCircle')
            .style.animation = 'dayNightCycle 5s';
          document.querySelector('.dayNightCircle')
            .style.animationFillMode = "forwards";

          document.querySelector('.dayNightCircle')
          .classList.add('nightTime');
          document.querySelector('.dayNightCircle')
          .classList.remove('dayTime');
          setTimeout(() => {
            for (let i = 0; i < document.querySelectorAll('.tile').length;i++) {
              document.querySelectorAll('.tile')[i].classList.add('tileNight')
            }
            for (let i = 0; i < document.querySelectorAll('.cloud').length;i++) {
              document.querySelectorAll('.cloud')[i].classList.add('cloudNight')
            }
          }, 5000)
      }
    } else if (this.props.is_day == 1) {
      if (!document.querySelector('.dayNightCircle')
      .classList.contains('dayTime')) {
        document.querySelector('.dayNightCircle')
        .style.animation = 'dayNightCycle 5s';
        document.querySelector('.dayNightCircle')
        .style.animationFillMode = "backwards";
        document.querySelector('.dayNightCircle')
        .style.animationDirection = "reverse";

        document.querySelector('.dayNightCircle')
        .classList.add('nightTime');
        document.querySelector('.dayNightCircle')
        .classList.remove('dayTime');
      }
    }
  }

  SunPos() {
    const now = new Date()
    console.log(now)
    const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const timesList = Object.values(sunPos)
    let sunState;
    for (let i = 0; i < timesList.length; i++) {
      if (now.getHours() == timesList[i].getHours()) {
        if (now.getMinutes() == timesList[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] == timesList[i]) {
                this.setState({
                  sunPos: sunPos[ii]
                })
              }
            }
            sunState = sunPos[timesList[i]];
            console.log(sunState)
          }
        }
      }
    }
  }
  
  componentDidMount() {
    this.checkSunState();
  }
  render() {
    return(
      <div className="dayNightCircle">
      <div className="snowContainer">
      </div>
        <div className="Sun"></div>
        <div className="Moon"></div>
      </div>
    )
  }
}
