import React from 'react'
import './WeatherTile.css';

export default class WeatherTile extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weather: null
    }
  }
  componentDidMount(props) {
  this.props.today ? this.setState({weather: 1}) : this.setState({weather: 0});

    const tiles = document.querySelectorAll('.tile')
    if (props.isday == 0) {
      for (var wtile in tiles) {
        wtile.classList.add('tileNight');
      }
    } else if (props.isday == 1) {
      for (var wtile1 in tiles) {
        wtile1.classList.remove('tileNight');
      }
    }
  }
  render(props) {
    let weather;
    this.props.today ? weather = <p>{this.props.today}</p> : weather = <p>{this.props.high} / {props.low}</p>

    return(
      <div className="tile">
        <img src={props.icon} alt='blak'/>
          <p className="tileDate">{props.date}</p>
          {weather  }
          <p id="tileCondition">{props.condition}</p>
      </div>
    )
  }
}
