import React from 'react'
import './App.css';
import WeatherTile from './WeatherTile'
import SearchBar from './SearchBar'

import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
    }
  }

  componentDidMount() {
}
  RequestWeather = () => {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=${this.state.location}&days=5`)
    .then(response => response.json())
    .then(json => {
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
        })
      }
    ).catch(() => {
      console.log('error')
    })
  }
  getLocation = (weather) => {
    console.log('APPLEVEL: ' + weather)
    this.setState({location: weather}, this.RequestWeather)
  }
  render() {
    return (
      <div className="App">
      <FullCalendar
        plugins={[ dayGridPlugin ]}
        initialView="dayGridMonth"
        events={[{title:'TEST', date:'2020-12-31'}]}
        aspectRatio='1.75'
        height='50%'
      />
      <SearchBar weatherCallBack={this.getLocation} />
        <div className="Tiles">
        {this.state.isLoading ?
        <WeatherTile icon={this.state.currentData.condition.icon} date="Now" today={this.state.currentData.temp_c} /> : null}
      {this.state.isLoading ?
        this.state.weatherData.map(data => (<WeatherTile icon={data.day.condition.icon} date={data.date} high={data.day.maxtemp_c} low={data.day.mintemp_c}/>)) : null }
        </div>
      </div>
    );
  }
}

export default App;
