import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.ShuffleNews = this.ShuffleNews.bind(this)

    this.state = {
      newsJson: null,
      headlines: null,
      shownHeadlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard/top.json')
    .then(res => res.json())
    .then(newsLinks => {
      let posts = [...newsLinks.data.children];
      console.log('POSTS11: ' + posts.length)

      this.setState({
        headlines: posts
    })
  })
}
/*const postAnim = (posts) => {

}*/

ShuffleNews() {
  let posts = [...this.state.headlines]
try {
  document.querySelectorAll('.newsHeadline').forEach(headline => {
    headline.classList.add('opacity0')
  })
  document.querySelector('.newsHeadline').addEventListener('transitionend', () => {
  this.setState({
    headlines: posts.slice(2,)
  })
    setTimeout(() => {
      document.querySelectorAll('.newsHeadline').forEach(headline => {
        headline.classList.remove('opacity0')
      })
    }, 1000)
  })
} catch {
  this.GetNews()
}

}

  componentDidUpdate(prevState) {
    if (this.state.headlines !== prevState.headlines) {
    //setTimeout(this.ShuffleNews, 6000 * 10)
    setTimeout(this.ShuffleNews, 1000 * 10)
    }
  }

  componentDidMount() {
    this.GetNews()
  }

  render() {
    console.log(this.state.headlines)
    return(
      <div className="news">
      {this.state.headlines != null ? this.state.headlines.slice(0,3).map(headline => {
        return <Headline title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : null}
      </div>
    )
  }
}
