import React from 'react'
import Icon from '@mdi/react'
import { mdiArrowDownDropCircleOutline } from '@mdi/js';
import './WeatherTile.css';

export default class WeatherTile extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weather: null
    }
  }
  componentDidMount(props) {
    if(this.props.today) {
      this.setState({weather: 1})
    } else {
      this.setState({weather: 0})
    }

    const tiles = document.querySelectorAll('.tile')
    if (props.isday == 0) {
      for (var wtile in tiles) {
        wtile.classList.add('tileNight');
      }
    } else if (props.isday == 1) {
      for (var wtile in tiles) {
        wtile.classList.remove('tileNight');
      }
    }
  }
  render(props) {
    return(
      <div className="tile">
        <img src={props.icon} alt='blak'/>
          <p className="tileDate">{props.date}</p>
          {this.state.weather == 1 ? <p>{this.props.today}</p> : <p>{this.props.high} / {props.low}</p>}
          <p id="tileCondition">{props.condition}</p>
      </div>
    )
  }
}
