import React from 'react'
import WeatherTile from './WeatherTile'
import './WeatherSection.css'

class WeatherSection extends React.Component {
  constructor(props) {
    super(props)
    this.RequestWeather = this.RequestWeather.bind(this)
    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
    }
  }
  RequestWeather() {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=3`)
    //fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=${this.state.location}&days=5`)
    .then(response => response.json())
    .then(json => {
      console.log(json)
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
        })
      }
    ).catch(() => {
      console.log('error')
    })
  }
  render() {
    <div className="weatherBackground">
    <div className="Tiles">
    {this.state.isLoading ?
    <WeatherTile icon={this.state.currentData.condition.icon} date="Now" today={this.state.currentData.temp_c} /> : null}
  {this.state.isLoading ?
    this.state.weatherData.map(data => (<WeatherTile icon={data.day.condition.icon} date={data.date} high={data.day.maxtemp_c} low={data.day.mintemp_c}/>)) : null }
    </div>
      <div className="weatherHouse"></div>
      <div className="weatherGround"></div>
    </div>
  }
}
export default WeatherSection
