import React from 'react'
import './DayNightCycle.css'
import suncalc from 'suncalc'

export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      sunPosition: null
    }
    //this.checkSunState = this.checkSunState.bind(this)
    this.SunPos = this.SunPos.bind(this)
  }

  SunPos() {
    const sun = document.querySelector('.Sun')
    const sky = document.querySelector('day')
    const sunYellow = document.querySelector('.sunYellow')
    const sunOrange = document.querySelector('.sunOrange')
    const sunRed = document.querySelector('.sunRed')

    const positions = ['noon', 'goldenHour', 'sunsetStart', 'sunset', 'dusk', 'nauticalDusk', '' ]
    const now = new Date()
    const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const sunTimes = Object.values(sunPos)
    for (let i = 0; i < sunTimes.length; i++) {
      if (now.getHours() == sunTimes[i].getHours()) {
        if (now.getMinutes() == sunTimes[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] == sunTimes[i])  {
                this.setState({
                  sunPosition: ii
                })
              }
            }
          }
        }
      }
    }
    if (this.state.sunPosition !== null) {
      if (this.state.sunPosition == 'noon') {

      } else if (this.state.sunPosition == 'goldenHour' || this.state.sunPosition == 'goldenHourEnd') {
          sky.classList.add('opacity3')
          sun.classList.add('goldenHour')
          sunYellow.classList.add('opacity2')
          sunOrange.classList.add('opacity2')

      } else if (this.state.sunPosition == 'sunsetStart' || this.state.sunPosition == 'sunriseEnd') {
        sky.classList.remove('opacity3')
        sky.classList.add('opacity2')
        sun.classList.remove('goldenHour')
        sun.add('sunsetStart')
        sunRed.classList.add('opacity2')

      } else if (this.state.sunPosition == 'sunset' || this.state.sunPosition == 'sunrise') {
          sky.classList.remove('opacity2')
          sky.classList.add('opacity1')
          sun.classList.add('sunset')
          sunYellow.classList.remove('opacity2')
          sunYellow.classList.add('opacity1')
          sunOrange.classList.remove('opacity2')
          sunOrange.classList.add('opacity3')
          sunRed.classList.remove('opacity2')
          sunRed.classList.add('opacity3')
      } else if ((this.state.sunPosition == 'dusk' || this.state.sunPosition == 'nauticalDusk') || (this.state.sunPosition == 'dawn' || this.state.sunPosition == 'nauticalDawn'))
          sky.classList.remove('opacity1')
          sky.classList.add('opacity0')
          sun.classList.remove('sunset')
          sun.classList.add('dusk')
          sunYellow.classList.remove('opacity1')
          sunYellow.classList.add('opacity0')
          sunOrange.classList.remove('opacity3')
          sunOrange.classList.add('opacity1')
      } else if (this.state.sunPosition == 'night' || this.state.sunPosition == 'nightEnd') {
          sun.classList.remove('dusk')
          sun.classList.add('night')
          sunOrange.classList.remove('opacity1')
          sunOrange.classList.add('opacity0')
          sunRed.classList.remove('opacity3')
          sunRed.classList.add('opacity1')
      } else if (this.state.sunPosition == 'natis') {
          sky.classList.add('skyNatis')
      }
    }


  componentDidMount() {
    console.log(suncalc.getTimes(new Date(), 51.5, -0.1))
    console.log(new Date())
    //this.checkSunState();
    const sunPosTimer = setInterval(this.SunPos, 30000)
  }
  InitSun() {
    
  }
  render() {
    return(
      <div className="night">
      <div className="day">
      </div>
        <div className="Sun">
          <div className="sunYellow"></div>
          <div className="sunOrange"></div>
          <div className="sunRed"></div>
        </div>
        <div className="Moon"></div>
      </div>
    )
  }
}
